print("{}".format(15))                               # this is print 15 is sting type because data type not define.
print("{:d}".format(15))                             # this is print 15 is integer type because data type {:d} is defined.
print("{0:d}".format(15))                            # 0: is a index number of 15.
print("{num:d}".format(num=15))                      # num: is a key value of 15.